#include <stdint.h>
#include "gpio.h"

void wait(uint32_t n) {
	for (int i = 0; i < n ; i++) {
		;
	}
}

int main(void)
{
	// Program initialization
	led_init();
	button_init();

	bool is_led_on = false;
	bool button_pressed;

	while (1) {
		// Get the button state
		button_pressed = get_button_state();

		if (button_pressed && !is_led_on) {
			led_on();
			is_led_on = true;
		}
		
		if (!button_pressed && is_led_on) {
			led_off();
			is_led_on = false;
		}

		wait(1);
	}
}
